  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'features',
	title: 'Features',
	slug: '/features'
};
export const metadata = {
  "unversionedId": "introduction/features",
  "id": "introduction/features",
  "isDocsHomePage": false,
